let writeKey = env.get('deviceKey');

if (!writeKey) {
    node.status({fill:"red",shape:"dot",text:"Missing Device Key"})
    return
} else {
    node.status({fill:"green",shape:"dot",text:"Device Key available"})
}

let deviceLogObj = {
    op: 'write',
    writeKey: writeKey,
    tag: []
}

for (let i in msg.truioTag) {
    deviceLogObj.tag.push({
        value: msg.truioTag[i].value,
        name: msg.truioTag[i].name,
        time: new Date().getTime()
    })
}

msg.payload = deviceLogObj
msg.topic = 'TruIO/device/' + writeKey
return msg;

